<style>
    .stat {
        display: flex;
        align-items: center;
        justify-content: space-between;
        font-size: 2rem;
    }

    .label {
        margin-right: 4rem;
    }

    .values {
        background-color: #F5FCA5;
        padding: 0.5rem 2rem;
        border-radius: 1rem;
    }

    .row {
        display: flex;
        align-items: center;
    }

    .row p {
        border: 1px solid #000;
        padding: 0 0.6rem;
        border-radius: 0.5rem;
        margin-left: 1rem;
    }

    .row-type {
        display: flex;
        align-items: center;
    }

    .row:not(:last-child) {
        margin-right: 4rem;
    }

    .stat-row {
        justify-content: center;
    }

    .row:first-child svg {
        position: relative;
        bottom: -4px;
        width: 4rem;
        cursor: pointer;
    }

    .values-flex {
        display: flex;
        align-items: center;
    }

    .values-flex p {
        margin-left: 2.5rem;
    }
</style>